package mainMenuCreating;import java.awt.Color;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.*;import game.maze.*;import game.pow211.NullLayout2048;import game.pow211.RunGUI;import mainMenuCreating.MainMenu_GUI;//import test.gui.run.RunGUI;public class GameSelectView extends JFrame {	public GameSelectView() {		setBounds(10, 10, 1000, 700);		// -------------------- 전체 Panel로서 3개의 공간과 메인 메뉴로 가는 버튼을 담는 영역		// -------------------------------------------------		JPanel wholePanel = new JPanel();		wholePanel.setLayout(null);		wholePanel.setBounds(0, 0, 1000, 700);		wholePanel.setBackground(Color.WHITE);		JButton goMain = new JButton();		goMain.setBounds(350, 550, 300, 100);		goMain.setText("Main");		goMain.setFont(goMain.getFont().deriveFont(20.0f));		goMain.addActionListener(new ActionListener() {			@Override			public void actionPerformed(ActionEvent e) {				JOptionPane.showMessageDialog(wholePanel, "Main Menu로 돌아갑니다. ");				if (e.getSource() == goMain) {					dispose();					new MainMenu_GUI();				}			}		});		wholePanel.add(goMain);		// -------------------- 2048게임 시작 버튼과 2048Label을 담는 영역		// ----------------------------------------		JPanel panel2048 = new JPanel();		panel2048.setLayout(null);		panel2048.setBackground(Color.YELLOW);		panel2048.setBounds(15, 100, 300, 400);		JLabel name2048 = new JLabel(); // Label로 변환해야 함.		name2048.setBounds(90, 50, 200, 100);		name2048.setText("2048");		name2048.setFont(name2048.getFont().deriveFont(60.0f));		panel2048.add(name2048);		JButton btn2048Start = new JButton("2048");		btn2048Start.setBounds(110, 250, 100, 50);		btn2048Start.setFont(btn2048Start.getFont().deriveFont(20.0f));		btn2048Start.addActionListener(new ActionListener() {			@Override			public void actionPerformed(ActionEvent e) {				JOptionPane.showMessageDialog(wholePanel, "2048 Game 화면으로 이동합니다.");				if (e.getSource() == btn2048Start) {					dispose();					new NullLayout2048();				}			}		});		panel2048.add(btn2048Start);		// -------------------- 미로찾기 시작 버튼과 미로찾기 Label을 담는 영역		// ----------------------------------------		JPanel panelMaze = new JPanel();		panelMaze.setLayout(null);		panelMaze.setBackground(Color.pink);		panelMaze.setBounds(340, 100, 300, 400);		JLabel nameMaze = new JLabel(); // Label로 변환해야 함.		nameMaze.setBounds(90, 50, 200, 100);		nameMaze.setText("Maze");		nameMaze.setFont(nameMaze.getFont().deriveFont(60.0f));		panelMaze.add(nameMaze);		JButton btnMazeStart = new JButton("Maze");		btnMazeStart.setBounds(110, 250, 100, 50);		btnMazeStart.setFont(btnMazeStart.getFont().deriveFont(20.0f));		btnMazeStart.addActionListener(new ActionListener() {			@Override			public void actionPerformed(ActionEvent e) {				JOptionPane.showMessageDialog(wholePanel, "Bone Maze Game을 시작합니다.");				if (e.getSource() == btnMazeStart) {					dispose();				}			}		});		panelMaze.add(btnMazeStart);		// -------------------- Simon 게임 시작버튼과 Simon게임 Label을 담는 영역		// ----------------------------------------		JPanel panelSimon = new JPanel();		panelSimon.setLayout(null);		panelSimon.setBackground(Color.blue);		panelSimon.setBounds(665, 100, 300, 400);		JLabel nameSimon = new JLabel();		nameSimon.setBounds(70, 50, 200, 100);		nameSimon.setText("Simon");		nameSimon.setFont(nameSimon.getFont().deriveFont(60.0f));		panelSimon.add(nameSimon);		JButton btnSimonStart = new JButton("Simon");		btnSimonStart.setBounds(110, 250, 100, 50);		btnSimonStart.setFont(btnSimonStart.getFont().deriveFont(20.0f));		panelSimon.add(btnSimonStart);		wholePanel.add(panel2048);		wholePanel.add(panelMaze);		wholePanel.add(panelSimon);		this.add(wholePanel);		setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		setVisible(true);	}}